import styled from "styled-components";
import { iconsImgs } from "../../utils/images";
// import SidebarContext from "./context/sidebarContext";

const MainContentTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 32px;
`;

const SidebarToggler = styled.button`
  display: flex;
  align-items: center;
  margin-right: 12px;
`;

const SidebarTogglerImg = styled.img`
  width: 20px;
`;

const ContentTopTitle = styled.h3`
  color: var(--clr-white);
  font-size: 20px;
  font-weight: 600;
`;

const ContentTopBtns = styled.div``;

const NotificationBtn = styled.button`
  margin-left: 18px;
`;

const NotificationBtnImg = styled.img`
  width: 24px;
`;

const ContentTop = () => {
  const { toggleSidebar } = useContext(SidebarContext);

  return (
    <MainContentTop>
      <div>
        <SidebarToggler type="button" onClick={toggleSidebar}>
          <SidebarTogglerImg src={iconsImgs.menu} alt="" />
        </SidebarToggler>
        <ContentTopTitle>My Tasks</ContentTopTitle>
      </div>
      <ContentTopBtns>
        <NotificationBtn className="content-top-btn">
          <NotificationBtnImg src={iconsImgs.bell} alt="" />
          <span className="notification-btn-dot"></span>
        </NotificationBtn>
      </ContentTopBtns>
    </MainContentTop>
  );
};

export default ContentTop;
