import styled from 'styled-components';



// Стили для компонента App
export const AppWrapper = styled.div`
  text-align: center;
`;

// Стили для заголовка h1
export const Heading = styled.h1`
  color: #ffffff;
  margin-bottom: 0.5rem;
  font-size: 1.75rem;
`;





// Стили для поля ввода todo-input
export const TodoInput = styled.input`
  outline: none;
  background: none;
  border: 1px solid #97abcc;
  padding: 0.5rem 1rem;
  margin-top: 1rem;
  margin-bottom: 2rem;
  width: 300px;
  color: #ffffff;

  &::placeholder {
    color: #ffffff4d;
  }
`;

// Стили для кнопки todo-btn
export const TodoButton = styled.button`
  background: #4a4a4a;
  color: #ffffff;
  border: none;
  padding: 0.55rem;
  cursor: pointer;
`;

// Стили для компонента Todo
export const TodoItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #4a4a4a;
  color: #ffffff;
  padding: 0.75rem 1rem;
  border-radius: 5px;
  margin-bottom: 1rem;
`;

// Стили для иконки fa-trash
export const TrashIcon = styled.i`
  margin-left: 0.75rem;
`;

// Стили для элементов с классом incompleted
export const IncompletedItem = styled.span`
  color: #ffffff;
  cursor: pointer;
`;

// Стили для элементов с классом completed
export const CompletedItem = styled.span`
  color: #b1b1b1;
  text-decoration: line-through;
  cursor: pointer;
`;

// Стили для иконок edit-icon и delete-icon
export const EditIcon = styled.i`
  cursor: pointer;
`;

export const DeleteIcon = styled.i`
  cursor: pointer;
`;
