const StyledTodo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #4a4a4a;
  color: #ffffff;
  padding: 0.75rem 1rem;
  border-radius: 5px;
  margin-bottom: 1rem;

  .incompleted {
    color: #ffffff;
    cursor: pointer;
  }

  .completed {
    color: #b1b1b1;
    text-decoration: line-through;
    cursor: pointer;
  }

  .edit-icon,
  .delete-icon {
    cursor: pointer;
  }
`;

export const Todo = ({ task, deleteTodo, editTodo, toggleComplete }) => {
  return (
    <StyledTodo>
      <p
        className={`${task.completed ? 'completed' : 'incompleted'}`}
        onClick={() => toggleComplete(task.id)}
      >
        {task.task}
      </p>
      <div>
        <FontAwesomeIcon
          className="edit-icon"
          icon={faPenToSquare}
          onClick={() => editTodo(task.id)}
        />
        <FontAwesomeIcon
          className="delete-icon"
          icon={faTrash}
          onClick={() => deleteTodo(task.id)}
        />
      </div>
    </StyledTodo>
  );
};
