import React, { useState } from "react";
import styled from "styled-components";
import { navigationLinks } from "../../data/data";
// import { SidebarContext } from "../../context/sidebarContext";

const SidebarContainer = styled.div`
  background-color: #2c2f36;
  width: 260px;
  padding: 36px 20px;

  &.sidebar-change {
    margin-left: -260px;
  }

  @media screen and (max-width: 1400px) {
    padding: 24px 16px;
  }

  @media screen and (max-width: 1200px) {
    width: 72px;
    padding-left: 12px;
    padding-right: 12px;

    &.sidebar-change {
      margin-left: -72px;
    }
  }

  @media screen and (max-width: 420px) {
    margin-left: -72px;

    &.sidebar-change {
      margin-left: 0px;
    }
  }
`;

const UserInfo = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: start;
  column-gap: 16px;
`;

const InfoName = styled.span`
  font-weight: 500;
  font-size: 20px;
  color: white;
  text-transform: uppercase;

  @media screen and (max-width: 1200px) {
    display: none;
  }
`;

const Navigation = styled.nav`
  margin-top: 28px;
  height: 450px;
  overflow-y: scroll;

  &::-webkit-scrollbar {
    width: 4px;
  }

  &::-webkit-scrollbar-track {
    box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 18px;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #75637b;
    border-radius: 18px;
  }
`;

// const NavList = styled.ul`
//   padding: 0;
//   margin: 0;
//   list-style: none;
// `;

const NavItem = styled.li`
  margin-bottom: 10px;
  margin-right: 4px;
`;

const NavLink = styled.a`
  display: block;
  height: 44px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: flex;
  padding: 6px 14px;
  column-gap: 12px;
  letter-spacing: 0.03em;
  border: 1px solid transparent;
  transition: var(--transition-default);

  &:hover {
    border: 1px solid var(--clr-primar-light);
  }

  &.active {
    background-color: var(--clr-pumpkin);
    box-shadow: rgba(0, 0, 0, 0.1) 0px 20px 25px -5px,
      rgba(0, 0, 0, 0.04) 0px 10px 10px -5px;
  }
`;

const NavLinkIcon = styled.img`
  width: 22px;
`;

const NavLinkText = styled.span`
  text-transform: capitalize;

  @media screen and (max-width: 1200px) {
    display: none;
  }
`;

const Sidebar = () => {
  const [activeLinkIdx] = useState(1);
  const [sidebarClass, setSidebarClass] = useState("");

  return (
    <SidebarContainer className={`sidebar ${sidebarClass}`}>
      <UserInfo>
        <InfoName>Username</InfoName>
      </UserInfo>

      <Navigation>
        <NavList>
          {navigationLinks.map((navigationLink) => (
            <NavItem key={navigationLink.id}>
              <NavLink
                href="#"
                className={`nav-link ${
                  navigationLink.id === activeLinkIdx ? "active" : ""
                }`}
              >
                <NavLinkIcon
                  src={navigationLink.image}
                  alt={navigationLink.title}
                />
                <NavLinkText>{navigationLink.title}</NavLinkText>
              </NavLink>
            </NavItem>
          ))}
        </NavList>
      </Navigation>
    </SidebarContainer>
  );
};

export default Sidebar;
