import React, { useEffect, useState, useContext } from 'react';
import styled, { css } from 'styled-components';
import { navigationLinks } from '../../data/data';

const SidebarWrapper = styled.div`
  background-color: var(--clr-primary);
  width: 260px;
  padding: 36px 20px;
  transition: var(--transition-default);

  ${(props) =>
    props.isSidebarOpen &&
    css`
      margin-left: -260px;
    `}

  @media screen and (max-width: 1400px) {
    padding: 24px 16px;
  }

  @media screen and (max-width: 1200px) {
    width: 72px;
    padding-left: 12px;
    padding-right: 12px;

    ${(props) =>
      props.isSidebarOpen &&
      css`
        margin-left: -72px;
      `}
  }

  @media screen and (max-width: 420px) {
    margin-left: ${(props) => (props.isSidebarOpen ? '-72px' : '0px')};
  }
`;

const UserInfo = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  column-gap: 16px;
`;

const InfoName = styled.span`
  font-weight: 500;
  font-size: 20px;
  color: var(--clr-white);
  text-transform: uppercase;

  @media screen and (max-width: 1200px) {
    display: none;
  }
`;

const Navigation = styled.nav`
  margin-top: 28px;
  height: 450px;
  overflow-y: scroll;

  &::-webkit-scrollbar {
    width: 4px;
  }

  &::-webkit-scrollbar-track {
    box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 18px;
  }

  &::-webkit-scrollbar-thumb {
    background-color: var(--clr-pumpkin);
    border-radius: 18px;
  }
`;

const NavItem = styled.li`
  margin-bottom: 10px;
  margin-right: 4px;
`;

const NavLink = styled.a`
  display: block;
  height: 44px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: flex;
  padding: 6px 14px;
  column-gap: 12px;
  letter-spacing: 0.03em;
  border: 1px solid transparent;
  transition: var(--transition-default);

  &:hover {
    border: 1px solid var(--clr-primar-light);
  }

  ${(props) =>
    props.isActive &&
    css`
      background-color: var(--clr-pumpkin);
      box-shadow: rgba(0, 0, 0, 0.1) 0px 20px 25px -5px,
        rgba(0, 0, 0, 0.04) 0px 10px 10px -5px;
    `}

  @media screen and (max-width: 1200px) {
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 32px;
    height: 32px;

    ${(props) =>
      props.isActive &&
      css`
        border-radius: 1000%;
      `}

    &:hover {
      border-radius: 100%;
    }
  }
`;

const NavLinkIcon = styled.img`
  width: 22px;
`;

const NavLinkText = styled.span`
  text-transform: capitalize;

  @media screen and (max-width: 1200px) {
    display: none;
  }
`;

const Sidebar = () => {
  const [activeLinkIdx] = useState(1);
  const [sidebarClass, setSidebarClass] = useState('');
  const { isSidebarOpen } = useContext(SidebarContext);

  useEffect(() => {
    if (isSidebarOpen) {
      setSidebarClass('sidebar-change');
    } else {
      setSidebarClass('');
    }
  }, [isSidebarOpen]);

  return (
    <SidebarWrapper className={`sidebar ${sidebarClass}`} isSidebarOpen={isSidebarOpen}>
      <UserInfo>
        <InfoName>Username</InfoName>
      </UserInfo>

      <Navigation>
        <ul className="nav-list">
          {navigationLinks.map((navigationLink) => (
            <NavItem className="nav-item" key={navigationLink.id}>
              <NavLink
                href="#"
                className={`nav-link ${navigationLink.id === activeLinkIdx ? 'active' : ''}`}
                isActive={navigationLink.id === activeLinkIdx}
              >
                <NavLinkIcon
                  src={navigationLink.image}
                  className="nav-link-icon"
                  alt={navigationLink.title}
                />
                <NavLinkText className="nav-link-text">{navigationLink.title}</NavLinkText>
              </NavLink>
            </NavItem>
          ))}
        </ul>
      </Navigation>
    </SidebarWrapper>
  );
};

export default Sidebar;
