import React, { useState } from "react";
import styled from 'styled-components';

// Создай новый стилизованный компонент StyledTodoForm
const StyledTodoForm = styled.form`
  .TodoForm {
    margin-bottom: 1rem;
  }

  .todo-input {
    padding: 0.5rem;
    border: none;
    border-radius: 5px;
    margin-right: 0.5rem;
    font-size: 1rem;
  }

  .todo-btn {
    padding: 0.5rem 1rem;
    border: none;
    border-radius: 5px;
    background-color: #4a4a4a;
    color: #ffffff;
    font-size: 1rem;
    cursor: pointer;
  }
`;

export const TodoForm = ({ addTodo }) => {
  const [value, setValue] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    addTodo(value);

    setValue("");
  };
  return (
    <StyledTodoForm>
      <input
        type="text"
        className="todo-input"
        value={value}
        placeholder="Enter your tasks"
        onChange={(e) => setValue(e.target.value)}
      />
      <button type="submit" className="todo-btn">
        Add Task
      </button>
    </StyledTodoForm>
  );
};
