import { createGlobalStyle } from "styled-components";

  const GlobalStyles = createGlobalStyle`

  @font-face {
    font-family: 'Poppins';
    src: url('./fonts/Poppins-Bold.ttf');
    font-weight: 700;
  }

  @font-face {
    font-family: 'Poppins';
    src: url('./fonts/Poppins-Regular.ttf');
    font-weight: 400;
  }

  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html {
	font-family: 'Poppins', sans-serif;
  }

  body {
    background: #191d20;
    display: flex;
    justify-content: center;
  }

  h1 {
	color: #fff;
  	margin-bottom: 0.5rem;
  	font-size: 1.75rem;
  }

  h2 {
  	color: #fff;
  	margin-bottom: 0.5rem;
  	font-size: 1.75rem;
  	text-align: center;
  }

  ul {
    list-style-type: none;
  }

  a {
    color: unset;
    text-decoration: none;
    color: #fff;
  }

  button {
    background-color: transparent;
    border: none;
    cursor: pointer;
  }

  .img-fit-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .text-white {
    color: #fff;
  }

  .text-silver-v1 {
    color: #bdbabb;
  }

  .text-scarlet {
    color: #fe1e00;
  }

  .text-green {
    color: #00fe93;
  }

  .bg-jet {
    background: #2c2f36;
  }

  .text-sm {
    font-size: 14px;
  }

  .app {
    display: flex;
    min-height: 100vh;
  }

  .lg-value {
    font-size: 24px;
    font-weight: 600;
  }

  .grid-common {
    background-color: #2c2f36;
    border-radius: 12px;
    box-shadow: rgba(0, 0, 0, 0.05) 0px 8px 24px;
    color: #fff;
    padding: 24px;
  }

  .grid-c-title {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 16px;
  }

  .grid-c-title-text {
    font-weight: 400;
    color: #a8a5a6;
  }

  .grid-c-title-icon img {
    width: 24px;
  }

  @media screen and (max-width: 1400px) {
    .grid-common {
      padding: 16px;
    }
    body {
      font-size: 14px !important;
    }
  }

`;

export default GlobalStyles;
