import React, { useState, useEffect } from "react";
import { Todo } from "../Todo/Todo";
import { TodoForm } from "../TodoForm/TodoForm";
import { v4 as uuidv4 } from "uuid";
import { EditTodoForm } from "../EditTodoForm/EditTodoForm";
import styled from "styled-components";

const StyledTodoWrapper = styled.div`
  width: 100%;
  background: #2c2f36;
  margin-top: 5rem;
  padding: 2rem;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  flex-wrap: wrap;
`;

const TabPanel = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 0.5rem;
  margin-top: 1rem;

  button {
    background: #75637b;
    color: #ffffff;
    border: none;
    padding: 8px 16px;
    cursor: pointer;
    border-radius: 5px;
    font-size: 16px;
    transition: transform 0.2s ease, background-color 0.2s ease;

    &:hover {
      background-color: #6c5b71;
    }

    &:active {
      transform: translateY(1px);
    }

    &:focus {
      outline: none;
    }
  }
`;

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);

  const STORAGE_KEY = "todoApp";

  const getTodosFromLocalStorage = () => {
    const todosString = localStorage.getItem(STORAGE_KEY);
    return todosString ? JSON.parse(todosString) : [];
  };

  const saveTodosToLocalStorage = (todosToSave) => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(todosToSave));
  };

  const [editingTodoId, setEditingTodoId] = useState(null);
  const [activeTab, setActiveTab] = useState("all");

  const addTodo = (task) => {
    if (task.trim() !== "") {
      const newTodo = {
        id: uuidv4(),
        task: task,
        completed: false,
      };
      setTodos([...todos, newTodo]);
    }
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const editTodo = (task, id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          task: task,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
    setEditingTodoId(null);
  };

  const toggleComplete = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          completed: !todo.completed,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleEditTodo = (id) => {
    setEditingTodoId(id);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  useEffect(() => {
    saveTodosToLocalStorage(todos);
  }, [todos]);

  useEffect(() => {
    setTodos(getTodosFromLocalStorage());
  }, []);

  const filteredTodos = todos.filter((todo) => {
    if (activeTab === "all") {
      return true;
    } else if (activeTab === "completed") {
      return todo.completed;
    } else if (activeTab === "incompleted") {
      return !todo.completed;
    }
    return true;
  });

  return (
    <StyledTodoWrapper>
      <h2>{activeTab.charAt(0).toUpperCase() + activeTab.slice(1)} Tasks</h2>
      <TabPanel>
        <button onClick={() => handleTabChange("all")}>All</button>
        <button onClick={() => handleTabChange("completed")}>Completed</button>
        <button onClick={() => handleTabChange("incompleted")}>Incompleted</button>
      </TabPanel>
      <TodoForm addTodo={addTodo} />
      {filteredTodos.map((todo) => (
        <div key={todo.id}>
          {editingTodoId === todo.id ? (
            <EditTodoForm editTodo={editTodo} task={todo} />
          ) : (
            <Todo
              task={todo}
              deleteTodo={deleteTodo}
              editTodo={handleEditTodo}
              toggleComplete={toggleComplete}
            />
          )}
        </div>
      ))}
    </StyledTodoWrapper>
  );
};
